$(".openmenubtn").click(function () {
    $(this).toggleClass("activebtn")
    $(".menu").slideToggle(500)
})

$(".link").on('click', function () {
    let name = $(this).attr('data-src')
    if($(window).width()<769) {
        $(".openmenubtn").removeClass("activebtn")
        $(".menu").slideUp(500)
    }
    $('html, body').animate({
        scrollTop: $(name).position().top - 100
    }, 500);
});

var collors_array = [
    "#2E2E2E","#756F6F","#FFF",
    "#600A4D","#FFE5E5","#600A4D",
    "#264D55","#7C9DA5","#FFF",
    "#564C95","#FFFFFF","#564C95"
]
var set_colors = 0;
$(".setcolorbtn").click(function(){
    set_colors +=3
    $(".setcolor1").css("background-color", collors_array[set_colors])
    $(".setcolor2").css("background-color", collors_array[set_colors+1])
    $(".setcolor3").css("color", collors_array[set_colors+2])
    $(".setcolor4 path").css("fill", collors_array[set_colors+2])
    if(set_colors >= collors_array.length) {
        set_colors = 0
        $(".setcolor1").css("background-color", collors_array[set_colors])
        $(".setcolor2").css("background-color", collors_array[set_colors+1])
        $(".setcolor3").css("color", collors_array[set_colors+2])
        $(".setcolor4 path").css("fill", collors_array[set_colors+2])
    }
    console.log(set_colors)
})